"use client";

import { CardContent } from "@/components/ui/card";
import { StepsHeader } from "./step-header";
import { useRouter, useSearchParams } from "next/navigation";
import React, {
  createContext,
  ReactNode,
  Suspense,
  useCallback,
  useContext,
} from "react";

export type UseStepActionsProps = {
  helpers: UseStepActions;
};

export type UseStepActions = {
  goToNextStep: () => void;
  goToPrevStep: () => void;
  reset: () => void;
  canGoToNextStep: boolean;
  canGoToPrevStep: boolean;
  setStep: (step: number | ((step: number) => number)) => void;
} & { currentStep?: number };

const StepContext = createContext<UseStepActions | null>(null);

export function useStep(): UseStepActions {
  const context = useContext(StepContext);
  if (context === null) {
    throw new Error("useStep must be used within a UserProvider");
  }
  return context;
}

type SetStepCallbackType = (step: number | ((step: number) => number)) => void;

function BaseStepProvider({ children }: { children: ReactNode }) {
  const maxStep = React.Children.count(children);
  const router = useRouter();
  const searchParams = useSearchParams();
  const currentStep = Number(searchParams.get("step") || "1");

  const canGoToNextStep = currentStep + 1 <= maxStep;
  const canGoToPrevStep = currentStep - 1 > 0;

  const setStep = useCallback<SetStepCallbackType>(
    step => {
      const newStep = typeof step === "function" ? step(currentStep) : step;

      if (newStep >= 1 && newStep <= maxStep) {
        const params = new URLSearchParams(searchParams);
        params.set("step", newStep.toString());
        router.push(`?${params.toString()}`);
        return;
      }

      throw new Error("Step not valid");
    },
    [maxStep, currentStep, router, searchParams],
  );

  const goToNextStep = useCallback(() => {
    if (canGoToNextStep) {
      setStep(step => step + 1);
    }
  }, [canGoToNextStep, setStep]);

  const goToPrevStep = useCallback(() => {
    if (canGoToPrevStep) {
      setStep(step => step - 1);
    }
  }, [canGoToPrevStep, setStep]);

  const reset = useCallback(() => {
    setStep(1);
  }, [setStep]);

  return (
    <StepContext.Provider
      value={{
        goToNextStep,
        goToPrevStep,
        canGoToNextStep,
        canGoToPrevStep,
        setStep,
        reset,
        currentStep,
      }}
    >
      <StepsHeader className="p-10" />
      {React.Children.map(children, (child, index) => {
        if (React.isValidElement(child) && index + 1 === currentStep) {
          return <CardContent>{React.cloneElement(child)}</CardContent>;
        }
        return null;
      })}
    </StepContext.Provider>
  );
}

// workaround hoc to prevent https://nextjs.org/docs/messages/missing-suspense-with-csr-bailout
export function StepProvider({ children }: { children: ReactNode }) {
  return (
    <Suspense>
      <BaseStepProvider>{children}</BaseStepProvider>
    </Suspense>
  );
}

import { CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { cn } from "@/lib/utils";
import { useStep } from "./step";

export const headerData = [
  {
    title: "First step",
    description: "This will blabla blabla blabla",
  },
  {
    title: "Second step",
    description: "This will blabla blabla blabla",
  },
  {
    title: "Third step",
    description: "This will blabla blabla blabla",
  },
] as const;

function Bounce() {
  return (
    <span className="relative flex h-2 w-2 pt-[.25rem]">
      <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-primary opacity-75"></span>
      <span className="relative inline-flex rounded-full h-2 w-2 bg-primary"></span>
    </span>
  );
}

export function StepsHeader({ className }: { className?: string }) {
  const { currentStep } = useStep();
  return (
    <CardHeader
      className={cn(
        "flex md:flex-row gap-8 md:gap-4 justify-around mt-8",
        className,
      )}
    >
      {headerData.map((step, i) => (
        <div
          key={i}
          className={`w-56 ${i + 1 !== currentStep && "opacity-40"} relative`}
        >
          <CardTitle className="flex gap-2 font-mono">
            <div className="z-20">{step.title}</div>
            {i + 1 === currentStep && <Bounce />}
          </CardTitle>
          <CardDescription className="italic">
            {step.description}
          </CardDescription>
          <div
            className={`
              absolute top-[-2rem] left-[-.8rem] text-muted-foreground font-bold text-6xl opacity-30
              before:content-[''] 
              before:absolute 
            `}
          >
            {i + 1}
          </div>
        </div>
      ))}
    </CardHeader>
  );
}
